import React, { Component } from "react";
import Axios from "axios";
import { navigate } from "@reach/router";


export default class IndividualArtwork extends Component {
  constructor(props) {
    super(props);

    this.state = { artworks: [] };
  }

  componentDidMount() {
    Axios.get(`http://localhost:9000/api/artworks/${this.props.id}`).then(
      res => {
        console.log(res.data);
        this.setState({ artworks: res.data });
      },
      error => {
        console.log("error = ", error);
      }
    );
  }

  render() {
    return (

        <div>

          <h2>helloss</h2>

              {this.state.artworks.map((artwork, i) => {

              return <h2>  artwork Title = {artwork.artwork_title}<br></br>artwork subtitle = {artwork.artwork_subtitle}<br></br>artworks price = {artwork.price}<br></br></h2>


            })}

            {/* <textarea></textarea>
            <button type="submit">submit</button> */}
             <div className="comment-wrapper">
        <form onSubmit={this.AddArtwork} ref={this.formRef}>

          <CategorySelector onCategoryUpdated={this.onCategoryUpdated} />
          <label>Artwork Title</label>
          <input type="text" name="artwork_title" placeholder="title" />

          <label>Artwork Subtitle</label>
          <input type="text" name="artwork_subtitle" placeholder="subtitle" />

          <label>Price</label>
          <input type="text" name="price" placeholder="price" />

          <input type="hidden" name="cat_id" value={this.state.cat_id} />


          <Button type="submit">Submit</Button>

        </form>
      </div>



        </div>

    );
  }
}
