import React, { Component } from "react";
import * as UTILS from "../utils";
import { navigate } from "@reach/router";
import Axios from "axios";


export default class UserArtworkInfo extends Component {
  constructor(props) {
    super(props);
    this.state = {
      artworks: [],
    };
  }

//   gotoEdit = (e) => {
//     let temp = this.props.id;
//     console.log(this.props.id);
//     navigate(`/edit-details/${temp}`);
//   };

  componentDidMount() {

      Axios.get(`${UTILS.artworks_url}/${this.props.id}`).then((res) => {
        console.table(res.data);
        this.setState({
            artworks: res.data,
        });
      });

  }

//   removeProduct = (evt) => {
//     var index = evt.target.getAttribute("data-uuid");
//     console.table(this.state.items);
//     Axios.delete(`${UTILS.show_items}/${this.props.id}`).then((res) => {
//       console.log(res.data);
//       navigate(`/user-items`);
//     });
//   };

  render() {
    return (
      <React.Fragment>

        <div className="artwork-detail page">
          {/* props not dispalying */}
          {this.state.artworks.map((artwork, i) => {
            return (
              <React.Fragment key={i}>
                <div className="detail-img-con">


                </div>

                <h2 className="dark">{artwork.title}</h2>

                <div className="price-size-con">
                  <h3 className="green">Price: {artwork.price}</h3>
                  <h3 className="green">Size: {artwork.size}</h3>
                  <h3 className="green">Con: {artwork.condition}</h3>
                </div>

                <p className="dark">{artwork.description}</p>


                <div className="edit-delete">
                  <button

                    _id={artwork._id}
                    onClick={this.removeProduct}
                  >
                    Delete
                  </button>
                  <button
                    className="btn btn-narrow  btn-primary"
                    onClick={this.gotoEdit}
                  >
                    Edit
                  </button>
                </div>
              </React.Fragment>
            );
          })}


        </div>

      </React.Fragment>
    );
  }
}
