import React, { Component } from "react";
import Axios from "axios";
import * as UTILS from "../utils";
import { navigate } from "@reach/router";
import DesignButton from "../buttons/DesignButton";
import SubNav from "./SubNav";
import TopNav from "./TopNav";
import 'bootstrap/dist/css/bootstrap.css'; 
import "../css/categories.css"


export default class Design extends Component {
  constructor(props) {
    super(props);

    this.state = { design: [] };
  }

  getDesignInfo = e => {
    navigate(`/IndividualArtwork/${this.props.id}`)

  };

  componentDidMount() {
    Axios.get(UTILS.category_design_url).then(res => {
      if (res.data.result === false) {
        this.setState({ result: false });
      } else {
        console.table(res.data);
        this.setState({ design: res.data, result: true });
      }
    });
  }

  render() {
    return (
      <React.Fragment>
        <TopNav />
        <SubNav />
<h3>DESIGNS</h3>



{this.state.design.map((design, i) => {
          return (

<div className="card">
  {/* <img src="./images/" class="card-img-top"> */}    
    <DesignButton
                design_title={design.artwork_title}
                key={design.design_price}
                id={design.id} />


    <h5 className="card-title"></h5>
    <p className="card-text"></p>

</div>
          );
        })}



{/* 
        {this.state.design.map((design, i) => {
          return (
            <li key={design.price}>
              <DesignButton
                design_title={design.artwork_title}
                id={design.id}
              />
            </li>
          );
        })} */}


      </React.Fragment>
    );
  }
}
