import React, { Component } from 'react'
import GlobalNav from './GlobalNav'
import Axios from "axios";
import * as UTILS from "../utils";
import { navigate } from "@reach/router";
import IllustrationButton from "./IllustrationButton.jsx";


export default class Illustration extends Component {
    constructor(props) {
        super(props);

        this.state = { artworks:[] };

      }

      GetArtworkInfo = e => {
        let temp = this.props.id;
        console.log(this.props._id);

        navigate(`/IndividualArtwork/${temp}`)



      };


      componentDidMount(){
        Axios.get(UTILS.add_artwork).then(
          res => {
            if (res.data.result === false) {
              this.setState({ result: false });
            } else {
              console.table(res.data)
              this.setState({artworks: res.data, result: true });
            }
          },

        );

      }

    render() {
        return (
            <React.Fragment>
                <GlobalNav />

                    {this.state.artworks.map((artwork, i) => {
      return <div onClick={this.GetArtworkInfo} >
artwork title = {artwork.artwork_title}<br></br>
artwork subtitle = {artwork.artwork_subtitle}
<IllustrationButton illustration_title={artwork.artwork_title} id={artwork.id} />


      </div>






    })}
            </React.Fragment>

        )
    }
}
