import React, { Component } from "react";
import * as UTILS from "../utils";
import { navigate } from "@reach/router";
import Axios from "axios";

import { Util } from "reactstrap";
import CategorySelector from "./CategorySelector";

export default class EditArtwork extends Component {
constructor(props) {
super(props);
this.state = { artworks: {}, isLoaded: false};
this.myRef = React.createRef();
}

onCategoryUpdated = e => {
    this.setState({ cat_id: e.target.value });
  };

gotoProducts = (e) => {
navigate(`/my-artworks`);
};



componentDidMount() {
Axios.get(`${UTILS.artworks_url}/${this.props.id}`).then((res) => {
this.setState({ artworks: res.data[0], isLoaded: true });
});
}

EditProduct = (e) => {
console.log("EDIT");
e.preventDefault();
// grab reference to the form data
var formData = new FormData(this.myRef.current);
//lets see what we have in the form
for (var p of formData.entries()) {
console.log(p);
}
var settings = {
headers: { "Content-Type": "multipart/form-data" },
};
Axios.put(`${UTILS.edit_artwork}/${this.props.id}`, formData, settings).then(
(res) => {
console.log(res);
},
(error) => {
console.log(this.props.id);
}
);
};

render() {
let {

image,
artwork_title,
price,
size,
condition,
description,
} = this.state.artworks;

// only append server url to images that are not external


return (
  <React.Fragment>

    <div className="page">
      <h2 className="page-tile">Edit Item</h2>
      <form
        className="form-wrapper"
        onSubmit={this.EditProduct}
        ref={this.myRef}
      >
        <input
          type="text"
          placeholder="Title"
          className="text-input"
          name="artwork_title"
          defaultValue={artwork_title}
        ></input>
        <input
          type="text"
          placeholder="Price"
          className="text-input"
          name="price"
          defaultValue={price}
        ></input>
        <input
          type="text"
          placeholder="Size"
          className="text-input"
          name="size"
          defaultValue={size}
        ></input>
        <input
          type="text"
          placeholder="Condition"
          className="text-input"
          name="condition"
          defaultValue={condition}
        ></input>
        {/*  V IMP - input name should match model name on server */}
        <input
          type="textarea"
          placeholder="Description"
          className="textarea-input"
          name="description"
          defaultValue={description}
        ></input>

        <CategorySelector onCategoryUpdated={this.onCategoryUpdated}/>


        <div >
          <figure>
            <img
              src={image}
              width="150px"
              height="150px"

            />

          </figure>
          <input
            type="file"
            name="image"
            src={image}
            defaultValue={image}
          ></input>
        </div>

        <button
          type="submit"
onClick={this.gotoProducts}
        >
          Update Artwork
        </button>
      </form>
    </div>


  </React.Fragment>
);
}
}
