import React, { Component } from "react";
import { navigate } from "@reach/router";

const initialState = {
  User: "",
  Email: "",
  Phone: "",
  Password: "",
  UserError: "",
  EmailError: "",
  PhoneError: "",
  PasswordError: "",
};

export default class SignupPage extends Component {
  constructor(props) {
    super(props);

    this.state = initialState;
  }

  UserChange = (e) => {
    this.setState({ User: e.target.value });
    localStorage.setItem("user", e.target.value);
  };

  EmailChange = (e) => {
    this.setState({ Email: e.target.value });
    localStorage.setItem("email", e.target.value);
  };

  PhoneChange = (e) => {
    this.setState({ Phone: e.target.value });
    localStorage.setItem("phone", e.target.value);
  };

  PasswordChange = (e) => {
    this.setState({ Password: e.target.value });
    localStorage.setItem("password", e.target.value);
  };

  validate = () => {
    let UserError = "";
    let EmailError = "";
    let PhoneError = "";
    let PasswordError = "";

    if (!this.state.User) {
      UserError = "Invalid name";
    }

    if (!this.state.Email.includes("@")) {
      EmailError = "invalid email must inlcude @";
    }

    if (!this.state.Email) {
      EmailError = "invalid email";
    }

    if (!this.state.Phone > 9) {
      PhoneError = "Invalid phone number should by 9 digits";
    }

    if (!this.state.Phone) {
      PhoneError = "invalid phone number";
    }

    if (!this.state.Password) {
      PasswordError = "Invalid password";
    }

    if (EmailError || UserError || PhoneError || PasswordError) {
      this.setState({ EmailError, UserError, PhoneError, PasswordError });
      return false;
    }
    return true;
  };

  onSubmit = (e) => {
    e.preventDefault();
    const isValid = this.validate();
    if (isValid) {
      console.log(this.state);
      this.setState(initialState);
      navigate("/profile-page");
    }
  };

  componentDidMount() {
    if (localStorage.getItem("user")) {
      this.setState({
        User: localStorage.getItem("user"),
      });
    }

    if (localStorage.getItem("email")) {
      this.setState({
        Email: localStorage.getItem("email"),
      });
    }

    if (localStorage.getItem("phone")) {
      this.setState({
        Phone: localStorage.getItem("phone"),
      });
    }
  }

  render() {
    return (
      <React.Fragment>
        <div>
          <form onSubmit={this.onSubmit}>
            <input
              type="text"
              placeholder="User"
              className="text-input"
              value={this.state.User}
              onChange={this.UserChange}
            ></input>
            <div>{this.state.UserError}</div>
            <input
              type="text"
              placeholder="Email"
              className="text-input"
              value={this.state.Email}
              onChange={this.EmailChange}
            ></input>
            <div>{this.state.EmailError}</div>
            <input
              type="number"
              placeholder="Phone"
              className="text-input"
              value={this.state.Phone}
              onChange={this.PhoneChange}
            ></input>
            <div>{this.state.PhoneError}</div>
            <input
              type="password"
              placeholder="Password"
              className="text-input"
              value={this.state.Password}
              onChange={this.PasswordChange}
            ></input>
            <div>{this.state.PasswordError}</div>

            <button type="submit">Sign up</button>
          </form>
        </div>
      </React.Fragment>
    );
  }
}
